(53, 'cms-test', 'CMS Test', 'كيلوغرام', '<h2 style=\"text-align:center\">CMS Test</h2>', '<h2 style=\"text-align:center\">CMS Test</h2>', '1638511929.jpg', '', '', 1, 44, 1, 'A', NULL, NULL, NULL, '', '', '', '', '', '', '', '', '', '', '', '', '<script type=\"application/ld+json\">\r\n{\r\n\"@context\": \"https://schema.org/\",\r\n\"@type\": \"Thing\",\r\n\"name\": \"Schema.org Ontology\",\r\n\"subjectOf\": {\r\n\"@type\": \"Book\",\r\n\"name\": \"The Complete History of Schema.org\"\r\n}\r\n}\r\n</script>', NULL, '2021-12-03 07:12:35', '2021-12-03 07:12:35', NULL);

