CREATE TABLE `cms_bkp12nov2021` (
  `id` bigint(20) NOT NULL,
  `slug_name` varchar(255) NOT NULL,
  `name_en` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `name_ar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `description_ar` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `description_en` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `banner_image` varchar(255) NOT NULL,
  `page_title_en` varchar(255) DEFAULT NULL,
  `page_title_ar` varchar(255) DEFAULT NULL,
  `displayabout` int(11) NOT NULL,
  `display_order` int(11) NOT NULL,
  `displayunderinvestor` int(11) NOT NULL,
  `status` enum('A','I') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'A=Active , I=Inactive',
  `slug` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `brochure_en` varchar(255) DEFAULT NULL,
  `brochure_ar` varchar(255) DEFAULT NULL,
  `brochure_label_en` varchar(255) DEFAULT NULL,
  `brochure_label_ar` varchar(255) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
