  `slug_name` varchar(255) NOT NULL,
  `name` varchar(255) NOT NULL,
  `name_ar` varchar(255) NOT NULL,
  `slogan` varchar(255) DEFAULT NULL,
  `slogan_ar` varchar(255) DEFAULT NULL,
  `banner` varchar(255) NOT NULL,
  `featured_image` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `project_logo` varchar(255) DEFAULT NULL,
  `heading` varchar(255) NOT NULL,
  `heading_ar` varchar(255) NOT NULL,
  `content_video` text DEFAULT NULL,
  `content` text NOT NULL,
  `content_ar` text NOT NULL,
  `short_description_en` longtext NOT NULL,
  `short_description_ar` longtext NOT NULL,
  `brochure` varchar(255) DEFAULT NULL,
  `brochure_ar` varchar(255) DEFAULT NULL,
  `map` text NOT NULL,
  `near_to` text NOT NULL,
  `services` text NOT NULL,
  `virtual_toure_image` text DEFAULT NULL,
  `project_status` varchar(255) NOT NULL,
  `bedroom` int(11) NOT NULL,
  `space` float(10,0) DEFAULT NULL,
  `city` int(11) NOT NULL,
  `type` varchar(255) NOT NULL,
  `featured` enum('0','1') NOT NULL DEFAULT '0',
  `video_thumbnail_image` varchar(255) DEFAULT NULL,
  `display_order` int(11) NOT NULL,
  `mobile_banner_image` varchar(255) DEFAULT NULL,
  `meta_title` varchar(255) DEFAULT NULL,
  `meta_descriptions` varchar(255) DEFAULT NULL,
  `meta_keyword` varchar(255) DEFAULT NULL,
  `alt_text` varchar(255) DEFAULT NULL,
  `canonical` varchar(255) DEFAULT NULL,
  `meta_title_ar` varchar(255) DEFAULT NULL,
  `meta_descriptions_ar` varchar(255) DEFAULT NULL,
  `meta_keyword_ar` varchar(255) DEFAULT NULL,
  `alt_text_ar` varchar(255) DEFAULT NULL,
  `canonical_ar` varchar(255) NOT NULL,
  `json_ld` text DEFAULT NULL,
  `global_site_tag` text DEFAULT NULL,
  `status` enum('0','1') NOT NULL DEFAULT '1',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `Brocher_en_chk` int(11) DEFAULT NULL,
  `Brocher_ar_chk` int(11) DEFAULT NULL,
  `reservation_en_chk` int(11) DEFAULT NULL,
  `reservation_ar_chk` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

